"font_color_h1_solid\",\"checkbox_margin_apply_all\":false,\"margin_right_unit\":\"px\",\"margin_right\":\"0\",\"margin_opp_left\":false,\"margin_opp_bottom\":false},\"breakpoint_tablet\":{\"font_size_h3_unit\":\"px\",\"font_size_h3\":\"30\",\"font_color_type_h3\":\"font_color_h3_solid\"},\"breakpoint_tablet_landscape\":{\"font_size_h3_unit\":\"px\",\"font_size_h3\":\"36\",\"font_color_type_h3\":\"font_color_h3_solid\"},\"checkbox_padding_apply_all\":\"1\",\"padding_top\":\"40\",\"b_sh_color\":\"#000000_0.07\",\"b_sh_spread\":\"5\",\"b_sh_blur\":\"10\",\"b_sh_vOffset\":\"0\",\"b_sh_hOffset\":\"0\",\"background_position\":\"50,50\",\"background_repeat\":\"repeat\",\"background_color\":\"#ffffff\",\"background_image-type\":\"image\",\"custom_parallax_scroll_zindex\":\"3\",\"margin_right\":\"-40\"}}]},{\"element_id\":\"jf9i588\",\"styling\":{\"_link_o\":\"no\"},\"modules\":[{\"mod_name\":\"image\",\"element_id\":\"ljlt588\",\"mod_settings\":{\"param_image\":\"regular\",\"width_image\":\"600\",\"url_image\":\"http:\\/\\/localhost\\/hiddenmind\\/wp-content\\/uploads\\/2023\\/09\\/woman-grayscale-600x479-1.jpg\",\"style_image\":\"image-center\",\"margin_left_unit\":\"%\",\"margin_left\":\"-10\",\"motion_effects\":{\"v\":{\"val\":{\"v_vp\":\"30,100\",\"v_speed\":\"1\",\"v_dir\":\"up\"}}},\"animation_effect_tab\":\"s_e_m\",\"breakpoint_mobile\":{\"margin_left_unit\":\"%\",\"margin_left\":\"0\",\"margin_bottom\":\"30\"},\"image_zoom_icon\":false,\"auto_fullwidth\":false,\"appearance_image\":false,\"caption_on_overlay\":false,\"custom_parallax_scroll_zindex\":\"1\"}}]}],\"sizes\":{\"desktop_gutter\":\"none\",\"tablet_landscape_size\":\"2\",\"mobile_size\":\"auto\",\"desktop_align\":\"center\",\"mobile_area\":\"\\\"2\\\" \\\"1\\\"\"}}]}],\"sizes\":[],\"styling\":{\"row_width\":\"fullwidth-content\",\"padding_bottom_unit\":\"%\",\"padding_bottom\":\"6\",\"padding_top\":\"6\",\"padding_top_unit\":\"%\",\"padding_opp_bottom\":\"1\",\"_link_o\":\"no\"}}]'),
