(58, 'wc-admin-wcpay-sweden-Q2-2023', 'marketing', 'en_US', 'WooCommerce Payments is now available in Sweden!', 'We’ve recently released WooCommerce Payments in Sweden. You can view and manage transactions right in your WordPress dashboard while securely accepting major cards, Apple Pay, and payments in over 100 currencies.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'google_uxstudy_recruitment_q3-2023', 'marketing', 'en_US', 'Share your feedback in exchange for $150', 'Woo has teamed up with Google to collect feedback on advertising products and services available to you. If selected, you’ll receive an incentive equal to $150 USD for a 60-minute interview that can help improve the products you use to advertise. If interested, please take 2 minutes to complete the screening survey.', '{}', 'pending', 'woocommerce.com', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2023-08-11 00:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-11 00:32:45', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2023-08-16 18:10:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-16 18:10:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-16 18:10:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-16 18:10:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-16 18:10:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Try the new way to get paid', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooPayments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>', '{}', 'unactioned', 'woocommerce-admin', '2023-08-18 18:13:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(75, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-08-24 19:59:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(76, 'tiktok_shopGTM_q3-2023_hasTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-07 00:44:13', NULL, 0, 'plain', '', 0, 0, 'info');
